mapper.select:
,m2.menu_name as parentName from sys_menu m left join sys_menu m2 on m.parent_id = m2.menu_id where 1 = 1
and m.status = #{pageInfo.status}
and m.type = #{pageInfo.type}
and m.menu_name like concat('%', #{pageInfo.keywords} ,'%')
order by m.order_number ASC

from sys_menu m left JOIN sys_role_menu srm on m.menu_id = srm.menu_id left join sys_user_role sur on sur.role_id = srm.role_id where sur.user_id = #{userId} AND 1 =1
and m.status = #{pageInfo.status}
and m.type = #{pageInfo.type}
and m.menu_name like concat('%', #{pageInfo.keywords} ,'%')
order by m.order_number ASC
